mapper.sql:
		1=1 
		
				 and Check_ID = #{ID}


				 and Check_CreateTime = #{CreateTime}


				 and Check_ModifyTime = #{ModifyTime}


				 and Check_Status = #{Status}


				 and Check_AdminID = #{AdminID}


				 and Check_Administrator = #{Administrator}


				 and Check_WarehouseID = #{WarehouseID}


				 and Check_Name = #{Name}


				 and Check_Time = #{Time}


				 and Check_Memo = #{Memo}


				 and Check_StoreID = #{StoreID}


				 and Check_AdjustStatus = #{AdjustStatus}


				 and Check_Operator = #{Operator}


				 and Check_Updater = #{Updater}


		
		
			and (
				Check_ID like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_CreateTime like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_ModifyTime like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_Status like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_AdminID like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_Administrator like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_WarehouseID like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_Name like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_Time like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_Memo like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_StoreID like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_AdjustStatus like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_Operator like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Check_Updater like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				
				1 != 1
			)
