mapper.sql:
		1=1 
		
				 and Role1_ID = #{ID}


				 and Role1_CreateTime = #{CreateTime}


				 and Role1_ModifyTime = #{ModifyTime}


				 and Role1_Status = #{Status}


				 and Role1_Name = #{Name}


				 and Role1_AdminID = #{AdminID}


		
		
			and (
				Role1_ID like CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Role1_CreateTime like CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Role1_ModifyTime like CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Role1_Status like CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Role1_Name like CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Role1_AdminID like CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				
				1 != 1
			)
