mapper.sql:
		1=1 
		
				 and Specification_ID = #{ID}


				 and Specification_CreateTime = #{CreateTime}


				 and Specification_ModifyTime = #{ModifyTime}


				 and Specification_Status = #{Status}


				 and Specification_Specs = #{Specs}


				 and Specification_Memo = #{Memo}


				 and Specification_ShopID = #{ShopID}


				 and Specification_Image = #{Image}


		
		
			and (
				Specification_ID like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Specification_CreateTime like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Specification_ModifyTime like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Specification_Status like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Specification_Specs like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Specification_Memo like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Specification_ShopID like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				Specification_Image like binary CONCAT(CONCAT('%', #{search,jdbcType=VARCHAR}),'%') or 
				
				1 != 1
			)
